class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans=0;
        int sum=0;
        int nr=k/2,pr=k-nr;
        int sign=1;
        for(int i=0;i<k;i++)
        {
            sum+=sign*a[i];
            sign*=-1;
        }
        if(sum==-nr||sum==pr)
            ans++;
        for(int i=0;i<a.size()-1;i++)
        {
            int t=i+k;
            if(t>=a.size())
                t=t%a.size();
            sum=sum*-1;
            sum+=a[i];
            if(k%2==1)
                sum+=a[t];
            else
                sum-=a[t];
            if(sum==-nr||sum==pr)
                ans++;

        }
        return ans;
    }
};
