class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

         int ans=0;
     for(int i=0;i<k;i++){
       c.push_back(c[i]);
     }

         int  n=c.size();

      vector<int>diff(n+1,0);
      for(int i=1;i<n;i++){
         if(c[i]!=c[i-1]) diff[i]+=diff[i-1]+1;
         else  diff[i]+=diff[i-1];
      }
      for(int i=k;i<n;i++){
         if(diff[i]-diff[i-k+1]==k-1)ans++;
      }




      return ans;
    }
};
