class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        vector<int> b=a;
        for(int i=0;i<b.size();i++)
            a.push_back(b[i]);

        int ans=0;
        int j=0;
        int i=0;
        while(j<n)
        {
            if(j+k-1>=2*n)
                break;

            while(i+1<2*n)
            {
                if(a[i]!=a[i+1])
                {
                    i++;
                }
                else
                    break;
            }

            if(i-j+1>=k)
                ans++;

            j++;
            i=max(i,j);
        }

        return ans;
    }
};
