class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>a=colors;
        int n=a.size(),c=0;
        auto w=[&](int x){
        	return a[x%n];
		};
		for(int i=1;i<k;i++)c+=w(i)!=w(i-1);
		int ans=0;
		for(int i=0;i<n;i++){
			ans+=(c==k-1);
			c-=w(i)!=w(i+1);
			c+=w(i+k-1)!=w(i+k);
		}
		return ans;
    }
};
