#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& array, int kValue) {
        extendArray(array, kValue);

        int result = calculateAlternatingGroups(array, kValue);

        return result;
    }

private:
    void extendArray(vector<int>& array, int kValue) {
        array.insert(array.end(), array.begin(), array.begin() + kValue - 1);
    }

    int calculateAlternatingGroups(const vector<int>& array, int kValue) {
        int groupCount = 0;
        int arrayLength = array.size();
        int groupNumber = 1;

        int index = 1;
        while (index < arrayLength) {
            if (array[index] != array[index - 1]) {
                groupNumber++;
            } else {
                groupNumber = 1;
            }

            if (groupNumber >= kValue) {
                groupCount++;
            }

            index++;
        }

        return groupCount;
    }
};
