class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int>v2;
        for(auto it: colors) v2.push_back(it);
        for(auto it: colors) v2.push_back(it);
        for(auto it: colors) v2.push_back(it);
        vector<int>v(3*n,0);
        for(int i = 1;i < v.size(); i++){
            if(v2[i] != v2[i -1]) v[i] = v[i - 1] + 1;
            else v[i] = 0;
        }
        // for(auto it : v){
        //     cout << it <<" ";
        // }
        // cout << endl;
        int count = 0;
        int i = n;
        while(i < 2 * n){
            if(v[i] - v[i - k + 1] == k -1){
                count++;
            }
            i++;
        }
        return count;
    }
};

