class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();


        int left=0;
        int right=1;
        int ans=0;

        while(right<=n+k-2 && left<n){

        if((left==right) || colors[right%n]!=colors[(right-1+n)%n]){
            right++;
        }
            else{

            left=right;
            right++;
            }
           if(right-left==k && left<n){
               left++;
               ans++;
           }



        }

        return ans;
    }
};
