class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int len = colors.size();
        int ans = 0;

        int idx = 0;

        while (idx < len) {
            bool ok = true;

            for (int j = 1; j < k; j++) {
                if (colors[(idx + j) % len] == colors[(idx + j - 1) % len]) {
                    ok = false;
                    idx = idx + j;
                }
            }

            if (ok) {
                ans++;
                idx++;

                while (idx < len) {
                    if (colors[(idx + k - 1) % len] != colors[(idx + k - 2) % len]) {
                        ans++;
                        idx++;
                    }
                    else {
                        idx = idx + k - 1;
                        break;
                    }
                }
            }
        }

        return ans;
    }
};
