class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0;
        int cnt = 0;
        int cn = 0;
        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] != colors[i + 1]) {
                ++cn;
            }
        }
        if (cn == k - 1) {
            ++cnt;
        }
        for (int i = 1; i < n; ++i) {
            if (colors[(i - 1) % n] != colors[i % n]) {
                --cn;
            }
            if (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]) {
                ++cn;
            }
            if (cn == k - 1) {
                ++cnt;
            }
        }
        return cnt;
    }
};
