class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }

        int ans = 0;
        int i = 1;
        int start = 0;

        while(start<n && i<2*n){
            if(colors[i] == colors[i-1]){
                start = i;
            }

            if(i-start+1>=k){
                ans++;
                start++;
            }
            i++;
        }

        return ans;
    }
};
