class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int len = 1;
        int cnt = 0;
        for(int i = 1; i < n + k - 1; i++) {
            if(v[i % n] != v[ (i - 1) % n]) len++;
            else {
                len = 1;
            }
            if(len == k) {
                cnt++;
                while(i + 1 < n + k - 1 && v[i % n] != v[(i + 1)% n]) {
                    cnt++;
                    i++;
                }
            }
        }
        return cnt;
    }
};
