class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n = colors.size(), cnt = 0;
        for(int i=0;i<k-1;i++){
            if(colors[i] != colors[i + 1])
                cnt++;
        }
        for(int i=0;i<n;i++){
            if(colors[(i + k-1) % n] != colors[(i + k) % n])
                cnt++;
            if(colors[i] != colors[(i + 1) % n])
                cnt--;
            if(cnt == k - 1)
                ans++;
        }
        return ans;
    }
};
