class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        vector<int>col;
        int n = colors.size();

            for(int i = 0;i<n;i++){
                col.push_back(colors[i]);
            }
             for(int i = 0;i<k-1;i++){
                col.push_back(colors[i]);
            }
              vector<int>dp(col.size()+1,-1);
            for(int i= k-1;i<col.size();i++){
                if (i==k-1){
                    for(int j= k-1;j>0;j--){
                        if (col[j]==col[j-1]){
                             dp[k-1]=j-1;
                             break;
                        }
                    }
                    if (dp[k-1]==-1){
                        ans++;
                    }
                }
                else{
                    dp[i]=dp[i-1];
                    if (col[i]==col[i-1]){
                        dp[i]=i-1;
                    }
                    else{
                        if (dp[i]==-1){
                            ans++;
                        }
                        else{
                            int el = i-dp[i]+1;
                            if (el>k){
                                ans++;
                            }
                        }
                    }
                }
            }
            return ans;
    }
};
