#define ll long long int
#define un unsigned long long
#define all(v) v.begin(),v.end()
#define pb push_back
#define sz(x) ((int)(x).size())

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=sz(c);
        vector<int> ne=c;
        for(int i=0;i<n;++i)
        {
            ne.pb(c[i]);
        }
        for(int i=0;i<n;++i)
        {
            ne.pb(c[i]);
        }
        int ans=0;
        int l=0,r=1;
        while(l<n)
        {
            if(r-l<k)
            {
                if(ne[r]!=ne[r+1])
                {
                    r++;
                }
                else
                {
                    l=r;
                    r++;
                }
            }
            else
            {
                ans++;
                l++;
            }
        }
        return ans;
    }
};
