class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int grp = 0;
        int n = colors.size();

        if (k == 1) 
        {
            return n;
        }

        int temp = 1;
        int i = 1;

        while (i < n + k - 1) {
            int now = i;
            int back = i - 1;

            if (now >= n) 
            {
                now -= n;
            }

            if (back >= n) 
            {
                back -= n;
            }

            if (colors[now] != colors[back]) 
            {
                temp++;
            }
            else 
            {
                temp = 1;
            }

            if (temp >= k) {
                grp++;
            }

            i++;
        }
        return grp;
    }
};
