class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k1) {
         vector<int>v=colors;
         v.insert(v.end(),v.begin(),v.begin() + k1-1);
         int i=0,j=1;

         for(size_t k = 1 ; k<=v.size()-1;k++)
         {
             if(v[k]!=v[k-1])
             {
                 j=j+1;
             }
             else{
                 j=1;
             }

             if(j>=k1){
                 ++i;
             }
         }
        return i;
    }
};
