class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for(int i=0;i<k;i++){
            c.push_back(c[i]);
        }
        int n = c.size();
        vector<int> vec(n,0);
        for(int i=1;i<n;i++){
            if(c[i]!=c[i-1]){
                vec[i] = vec[i-1]+1;
            }
        }
        int ans = 0;
        for(int i=0;i+k<n;i++){
            // cout<<vec[i]<<" "<<vec[i+k-1]<<endl;
            if(vec[i]<vec[i+k-1] && vec[i+k-1]-vec[i]+1 == k){
                ans++;
            }

        }
        // cout<<endl;
        return ans;
    }
};
