class Solution:
    def numberOfAlternatingGroups(self, C, k) -> int:
        C = C + C[:k-1]
        R, N = 0, 1
        for i in range(1, len(C)):
            if C[i] != C[i-1]:
                N += 1
            else:
                N = 1
            if N >= k:
                R += 1
        return R
