class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) 
    {
        int ans = 0;
        vector<int> a = v;
        for(int i = 0; i < k - 1; i++) a.push_back(v[i]);

        int n = a.size();
        vector<int> pre(n, 0);
        pre[0] = 1;
        for(int i = 1; i < n; i++)
        {
            if(a[i] != a[i - 1]) pre[i] = pre[i - 1] + 1;
            else pre[i] = 1;
        }

        for(int i = k - 1; i < n; i++)
        {
            if(pre[i] >= k) ans++;
        }

        return ans;
    }
};
