class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int N = colors.size();
        int count = 0;

        if (is_SCase(k)) {
            return h_SCase(N);
        }

        int alt_L = 1;
        int i = 1;
        while (i < N + k - 1) {
            p_CIndex(colors, N, k, i, alt_L, count);
            ++i;
        }

        return count;
    }

private:
    bool is_SCase(int k) {
        return k == 1;
    }

    int h_SCase(int N) {
        return N;
    }

    void p_CIndex(vector<int>& colors, int N, int k, int i, int& alt_L, int& count) {
        int curr = adj_I(i, N);
        int prev = adj_I(i - 1, N);

        if (is_Alt(colors, curr, prev)) {
            ++alt_L;
        } else {
            res_Alt(alt_L);
        }

        if (is_l_En(alt_L, k)) {
            ++count;
        }
    }

    int adj_I(int index, int N) {
        if (index >= N) {
            index -= N;
        }
        return index;
    }

    bool is_Alt(const vector<int>& colors, int curr, int prev) {
        return colors[curr] != colors[prev];
    }

    void res_Alt(int& alt_L) {
        alt_L = 1;
    }

    bool is_l_En(int alt_L, int k) {
        return alt_L >= k;
    }
};
