class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int i = 0, j = 1;
        int n = colors.size();
        for(int i = 0; i < n; i++){
            colors.push_back(colors[i]);
        }
        while(j < n + k and i < n){
            int start = j;
            int end = start;
            while(colors[end] != colors[end - 1] and (end < (n + k - 1))){
                start++;
                end = start;
            }
            ans += max(0, start - i - k + 1);
            i = start;
            j = i + 1;
        }
        return ans;
    }
};
