class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans=0;
        int t=1;
        int p=1;
        int b=c.size();
        int curr=c[0];
        while(p<(b-1+k)){
            if(c[p%b]!=curr){
                t++;
                curr=c[p%b];
            }
            else{
                curr=c[p%b];
                t=1;
            }
            if(t==k){
                ans++;
                t=k-1;
            }
            p++;
        }
        return ans;
    }
};
