class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> pf(colors.size(),0);
        for(int i=0;i<n;i++){
            if(colors[(i-1+n)%n]==colors[(i+1)%n] && colors[i]!=colors[(i+1)%n]){
                pf[i]+=pf[(i-1+n)%n]+1;
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if(i+k-2>=n){
                if(pf[n-1]-pf[i]+pf[(i+k-2)%n]==k-2) ans++;
            }
            else if(pf[i+k-2]-pf[i]==k-2) ans++;
        }
        return ans;
    }
};
