class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans = 0;
        if(k == 1) return n;
        int len = 1;
        int i = 1;
        while(i < n+k-1){
            int curr = i%n;
            int prev = (i-1)%n;
            if(colors[curr] != colors[prev]) len++;
            else len = 1;
            if(len >= k) ans++;
            i++;
        }
        return ans;
    }
}
