class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
    int count = 0;
    int startIndex = 0;
    int endIndex = 1;
    int ans = 0;

    while (endIndex < colors.length + k && startIndex < colors.length) {
        int tempEndIndex = endIndex;
        int tempIndex = tempEndIndex % colors.length;
        while (colors[tempIndex] != colors[(tempIndex - 1 + colors.length) % colors.length] && tempEndIndex < (colors.length + k - 1)) {
            tempEndIndex++;
            tempIndex = tempEndIndex % colors.length;
        }

        ans += Math.max(tempEndIndex - startIndex - k + 1, 0);
        startIndex = tempEndIndex;
        endIndex = startIndex + 1;
    }
    return ans;
    }
}
