class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), cnt = 0;

        vector<int>v(colors.begin(), colors.end());

        for(int i = 0; i < k - 1; i++)
            v.push_back(colors[i]);

        int next = 1;
        for(int i = 1; i < v.size(); i++) {
            if(v[i] != v[ i - 1])
                next++;
            else
                next = 1;
            cnt += next >= k;
        }
        return cnt;
    }
};
