class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n = colors.size(), streak = 1;
        for(int i = 1; i < n + k - 1; i++) {
            int pos = i % n;
            if(colors[pos] != colors[(pos - 1 + n) % n]) streak++;
            else streak = 1;
            if(streak >= k) ans++;
        }
        return ans;
    }
};
