class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int m = colors.size();
        vector<int> v;
        for(int i = m-k+1; i<m; ++i){
            v.push_back(colors[i]);
        }
        for(int i = 0; i<m; ++i){
            v.push_back(colors[i]);
        }
        // for(int i = 0; i<k-1; ++i){
        //     v.push_back(colors[i]);
        // }
        int n = v.size();
        int ans = 0;
        int len = 1;

        for(int i = 0; i<n; ++i) cout<<v[i]<<" ";

        for(int i = 1; i<n; ++i){
            if(v[i]==v[i-1]){
                len = 1;
            }
            else{
                ++len;
            }
            if(len==k){
                ++ans;
                len--;
            }
        }
        return ans;
    }
};
