class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        cnt = 0
        streak = 1
        for i in range(1, len(colors) + (k - 1)):
            if colors[i % len(colors)] != colors[(i - 1) % len(colors)]:
                streak += 1
            else: 
                streak = 1

            if streak >= k:
                cnt += 1

        return cnt
