#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size();
        int cnt = 0;
        if (k > n) return 0; 
        int l = 0; // start
        int r = l + k - 1; // end
        for (int i = 1; i < n + k - 1; ++i) {
            int cur = i % n; // currIndex
            int prev = (i - 1) % n; // prevIndex
            if (col[cur] == col[prev]) {
                l = cur; 
                r = (l + k - 1) % n;
            }
            if ((i % n) == r) {
                cnt++;
                l++;
                r = (l + k - 1) % n;
            }
        }

        return cnt;
    }
};
