class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        auto calc = [&colors](int k) -> int {
            int ans = 0;
            for (int i = 0; i < k - 1; i++) {
                colors.push_back(colors[i]);
            }

            int cnt = 1;
            for (size_t i = 1; i < colors.size(); i++) {
                cnt = colors[i] == colors[i - 1] ? 1 : cnt + 1;
                ans += cnt >= k;
            }
            return ans;
        };

        return calc(k);
    }
};
