class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        int ans=0;
        if(k==1){return n;}int a=1;int i=1;
        int x=n+k-1;
        while(i<x){
            int cur=i;
            int pre=i-1;if(cur>=n){
                cur-=n;
            }if(pre>=n){pre-=n;}
            if(c[cur]!=c[pre]){a++;}else{
                a=1;
            }if(a>=k){ans++;}i++;
        }return ans;


    }
};
