class Solution
{
public:
    int numberOfAlternatingGroups(std::vector<int> &nums, int k)
    {
        int n = nums.size();
        if (k > n)
            return 0;

        for (int i = 0; i < k - 1; i++)
        {
            nums.push_back(nums[i]);
        }

        int left = 0, right = 0, ans = 0;
        int m = nums.size();

        for (int i = 0; i < m - 1; i++)
        {
            if (nums[i] == nums[i + 1])
            {
                left = i + 1;
                right = i + 1;
            }
            else
            {
                right++;
                if (right - left + 1 == k)
                {
                    ans++;
                    left++;
                }
            }
        }

        return ans;
    }
};
