class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans = 0;
        int n = a.size();
        int r = 0;
        int cnt = 0;
        for (; r < k - 2; r++) {
            cnt += a[r] ^ a[r + 1];
        }
        for (int l = 0; l < n; l++) {
            cnt += a[r] ^ a[(r + 1) % n];
            // cout << cnt << " ";
            if (cnt == k - 1)
                ans++;
            r = (r + 1) % n;
            cnt -= a[l] ^ a[(l + 1) % n];
        }
        return ans;
    }
};
