class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int j = 0;
        int n = a.size();
        for(int i = 0; i < n; i++) a.push_back(a[i]);
        int ans = 0;
        for(int i = 0; i < n; i++){
            j = max(j, i);
            while(j < n + n - 1 && a[j + 1] != a[j]) j++;
            if(j - i + 1 >= k) ans++;
        }    
        return ans;
    }
};
