class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const unsigned n = colors.size();
        for (unsigned i = 0; i < k - 1; ++i)
            colors.push_back(colors[i]);
        int ret = 0;
        for (unsigned i = 0, j = 0; i < n + k - 1; ++i) {
            if (j == i) {
                ++j;
                while(j < n + k - 1 && colors[j] != colors[j - 1])
                    ++j;
            }
            if (j - i >= k)
                ++ret;
        }
        return ret;
    }
};
