class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int>arr=colors;
        int idx=0;
        for(int cnt=2;cnt<=k;cnt++){
            arr.push_back(colors[idx]);
            idx++;
        }
        queue<int>q;
        for(int i=1;i<arr.size()-1;i++){
            if(arr[i]==arr[i-1]||arr[i]==arr[i+1])q.push(i);
        }
        int ans=0;
        for(int i=0;i<n;i++){
            while(!q.empty()&&q.front()<=i)q.pop();
            if(q.empty()||q.front()>=(i+k-1))ans++;
        }
        return ans;

    }
};
