class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int res = 0; int cnt = 0;
        int i = 0; int j = 0;
        while (i < n) {
            j ++;
            if (colors[j % n] == colors[(j - 1 + n) % n]) cnt ++;
            if (j - i + 1 > k) {
                if (i + 1 < n && colors[i + 1] == colors[i]) cnt --;
                i ++;
            }
            if (i != n && j - i + 1 == k && cnt == 0) res ++;
        }
        return res;

    }
}
