class Solution {
public:
    bool helper(vector<int>&colors, int k, int idx){
        int curr = colors[idx];
        for(int i=1;i<k;i++){
            int next = colors[(idx+i)%(int)colors.size()];
            if(curr == next){
                return false;
            }
            else{   
                curr = next;
            }
        }
        return true;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       for(int i=0;i<k;i++){
           colors.push_back(colors[i]);
       }
        int counter = 0;
        vector<int>pref(colors.size()+1,0);
        for(int i=1;i<colors.size();i++){
            pref[i] +=pref[i-1]+abs(colors[i]-colors[i-1]);
        }
        k-=1;
        for(int i=k+1;i<=colors.size()-1;i++){
            if(pref[i]-pref[i-k] == k){
                counter++;
            }
        }
        return counter;
    }
};
