class Solution {
public:
    int numberOfAlternatingGroups(std::vector<int>& arr, int k) {
        std::vector<int> ret;
        int i = 0;
        int j = 1;
        int num_found = 0;
        int n = arr.size();

        while (j < n + k && i < n) {
            int temp_j = j;
            int temp_end = temp_j % n;
            while (arr[temp_end] != arr[(temp_end - 1 + n) % n] && temp_j < n + k - 1) {
                temp_j++;
                temp_end = temp_j % n;
            }

            num_found += std::max(temp_j - i - k + 1, 0);
            i = temp_j;
            j = i + 1;
        }
        return num_found;
    }
};
