class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int cnt=0;
        int siz =1;
        for(int i=0;i<n+k-2;i++){
            if(v[i%n]!=v[(i+1)%n]){
                siz++;
            }
            else{
                siz=1;
            }
            // cout<<siz<<" ";
            if(siz==k){
                cnt++;
                siz--;
            }


            // cnt++;
        }
        return cnt;
    }
};
