class Solution {
public:

    void insert(deque<pair<int, int>> &q, int el){
        if(q.empty()){
            q.push_back({1, el});
        }
        else{
            if(q.back().second == el)   q.back().first++;
            else q.push_back({1, el});
        }
    }

    void remove(deque<pair<int, int>> &q){
        if(q.empty())   return;
        if(q.front().first > 1) q.front().first--;
        else q.pop_front();
    }

    int numberOfAlternatingGroups(vector<int>& c, int k) {
        for(int i=0; i<k-1; i++)    c.push_back(c[i]);
        int ans = 0;
        deque<pair<int, int>> q;

        for(int i=0; i<k-1; i++)   insert(q, c[i]);
        int n = c.size();

        for(int i=k - 1; i<n; i++){
            insert(q, c[i]);
            ans += (q.size() == k);
            remove(q);
        }

        return ans;
    }
};
