class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0,n=v.size();
        for(int i=0;i<n;i++){
            v.push_back(v[i]);
        }
        if(k==1) return n;
        vector<vector<int>> u;
        int l=-1;
        for(int i=1;i<k;i++){
            if(v[i]==v[i-1]){
                l=i-1;
            }
        }
        if(l<0){
            ans++;
        }
        for(int i=k;i<(n+k-1);i++){
            if(v[i]==v[i-1]){
                l=i-1;
            }
            if(l<(i-k+1)){
                ans++;
            }
        }
        return ans;
    }
};
