class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int nf = 0;
        int s = 0;
        int e = 1;
        int n = colors.size();

        while (e < n + k && s < n) {
            int te = e % n;

            while (colors[te] != colors[(te - 1 + n) % n] && e < n + k - 1) {
                e++;
                te = e % n;
            }

            nf += max(e - s - k + 1, 0);
            s = e;
            e = s + 1;
        }

        return nf;
    }
};
