class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i, n = colors.size() + k - 1, ans = 0, cnt = 0;
        for (i = 0; i < k - 1; i++) colors.push_back(colors[i]);
        for (i = n; --i;) colors[i] = colors[i] == colors[i - 1];
        for (i = 1; i < k; i++) cnt += colors[i];
        ans += !cnt;
        for (i = k; i < n; i++) {
            cnt += colors[i] - colors[i - k + 1];
            ans += !cnt;
        }
        return ans;
    }
};
