func numberOfAlternatingGroups(colors []int, k int) int {
    N := len(colors)
    c := 0

    if k == 1 {
        return N
    }

    al := 1
    i := 1

    for i < N + k - 1 {
        cr := i
        pr := i - 1

        if cr >= N {
            cr -= N
        }
        if pr >= N {
            pr -= N
        }

        if colors[cr] != colors[pr] {
            al++
        } else {
            al = 1
        }

        if al >= k {
            c++
        }

        i++
    }

    return c
}
