class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0 ; 
        int laind = 0 , last = colors[0];
        for ( int i = 0 ; i < k -1 ; i ++ ){
            colors.push_back ( colors[i]);
        }
        for ( int i = 1 ; i < colors.size() ; i ++  ){
            if ( colors[i] != last ){
                last = colors[i];
            }
            else { 
                if ( i - laind >= k ){
                    ans += i - laind - k + 1 ; 
                }
                last = colors[i];
                laind = i ; 
            }
        }
        if ( colors.size() - laind >= k ){
            ans += colors.size() - laind - k +1 ; 
        }
        return ans ; 
    }
};
