const int N = 1e5 + 5;
int dp[N];
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& tab, int k) {
        int n=tab.size();
        for(int i=0;i<=n;i++) dp[i]=0;
        dp[0]=1;
        for(int i=1;i<n;i++){
            if(tab[i-1]!=tab[i]) dp[i]=dp[i-1]+1;
            else dp[i]=1;
        }
        int ans=0;
        for(int i=k-1;i<n;i++){
            if(dp[i]>=k) ans++;
        }
        if(tab[n-1]!=tab[0]) dp[0]=dp[n-1]+1;
        else dp[0]=1;
        for(int i=1;i<k-1;i++){
            if(tab[i-1]!=tab[i]) dp[i]=dp[i-1]+1;
            else dp[i]=1;
        }
        for(int i=0;i<k-1;i++){
            if(dp[i]>=k) ans++;
        }
        return ans;
    }
};
