class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> v;
        for (int i = 0; i < n + k - 1; i++) {
            if (colors[i % n] != colors[(i + 1) % n]) {
                v.push_back(1);
            } else {
                v.push_back(0);
            }
        }
        k--;
        int s = 0, ans = 0;
        for (int i = 0; i < n + k - 1; i++) {
            s += v[i];
            if (i >= k) s -= v[i - k];
            if (i >= k - 1 && s == k) ans++;
        }
        return ans;
    }
};
