class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0, alt=1;
        int i=0, j=1;
        while(i<n) {
            // cout << i << endl;
            while(alt<k && colors[j%n] != colors[(j-1)%n]) {
                j++;
                alt++;
            }

            if (alt == k) {
                ans++;
                i++;
                alt--;
            } else {
                i = j;
                j++;
                alt = 1;
            }
        }  

        return ans;      
    }
};
